mapper.insert:
insert into member (
	custid
	, password
	, custname
	, name
	, custtel
	, tel
	, postcode
	, road
	, jibun
	, idno
	, division
	, lat
	, lng
) values (
	#{custid}
	, #{password}
	, #{custname}
	, #{name}
	, #{custtel}
	, #{tel}
	, #{postcode}
	, #{road}
	, #{jibun}
	, #{idno}
	, #{division}
	, #{lat}
	, #{lng}
)

mapper.update:
update member
set
	password = #{password}
	, custname = #{custname}
	, name = #{name}
	, custtel = #{custtel}
	, tel = #{tel}
	, postcode = #{postcode}
	, road = #{road}
	, jibun = #{jibun}
	, idno = #{idno}
	, division = #{division}
	, lat = #{lat}
	, lng = #{lng}
where
	custid = #{custid}
